// import React, { useState, useEffect } from 'react'
// import { motion } from 'framer-motion';
// import { urlFor, client } from '../client';
import { useState } from "react";
import { client, urlFor } from '../client';


function TextEntry() {



  const [selectedFile, setSelectedFile] = useState("");
  const [preview, setPreview] = useState("");


  const handleKeyDown = (event) => {
    if (event.key === 'Enter') {
      // ðŸ‘‡ Get input value
      // console.log("Message: " + event.target.value);
      // console.log(selectedFile);


      // const doc = {
      //   _type: 'message',
      //   content: event.target.value,
      //   profile: "Elygh Thao",
      // }
      // client.create(doc).then((res) => {
      //   console.log(`Bike was created, document ID is ${res._id}`)
      // })

      // client.delete({query: '*[_type == "message" && _id == "Pfyhvf2287QNe3dmP9z5Ot"][0...999]'})

      client.assets.upload('image', preview, {contentType: 'image/png', filename: 'someText.png'})
      
      event.target.value = "";
      setSelectedFile("");
      setPreview("");
    }
  };



  return (
    <div className="
      bg-blue-500 
      w-screen 
      h-10
      auto-cols-auto
      fixed
      flex
      flex-row
      bottom-0
      ">



      <div className="
        
        w-3/4
        flex
        p-1
        ">
        <input type="text"
          id="message"
          onKeyDown={handleKeyDown}
          className="w-screen" />
      </div>

      <div className="
        flex
        justify-center
        w-1/4">
        <input type="file"
          className="opacity-0 -z-3 fixed w-1/4"
          onChange={(e) => {
            setSelectedFile(e.target.files[0]);
            setPreview(URL.createObjectURL(e.target.files[0]));
          }} />
        <img src={preview} alt=" " className="
            w-1/3"/>
      </div>









    </div>
  );
}

export default TextEntry;
